var myStreamingTag = new ns_.StreamingTag( { customerC2: '17935924', C3: 'deutch' } );
    var metadata = {
        ns_st_ci: "5f4e2cd7b480cc36e129b01a", // идентификатор контента (видео)
        c3: "deutch",
        c4: "*null",
        c6: "*null" 
    };


    jwplayer.key="kESrsgULvYO7r2MfUelqj3vs+2i8JdQBqjGNmA==";

    jwplayer("js-mediaplayer-5f4e2cd7b480cc36e129b01a").setup({
        advertising: {
          client: "vast",

            tag: 'http://pubads.g.doubleclick.net/gampad/ads?sz=640x480&iu=/144751494/ES_Video_Articles&ciu_szs&impl=s&gdfp_req=1&env=vp&output=xml_vast2&unviewed_position_start=1&url='+encodeURIComponent(location.href)+'&description_url='+encodeURIComponent(location.href)+'&correlator='+Date.now()          

        },
        file:        "https://cdnv.rt.com/deutsch/video/2020.09/5f4e2cd7b480cc36e129b01a.mp4",
                        // title:       "Balkan\u002DMigrationsroute",
        aspectratio: "16:9",
        skin: {
            name: "five",
            active: "#77bd1e",
            background: "rgba(0, 0, 0, 0.5)",
            inactive: "#FFFFFF"

        },
        width:       "100%",
        startparam:  "start",
        ga: {},
        events: {
            onPlay: function(){
                if(ga) ga('send', 'event', 'JWPLAYER-GA', 'CLICK PLAY', location.href);
                myStreamingTag.playVideoContentPart(metadata);
            },
            onPause: function(){
                if(ga) ga('send', 'event', 'JWPLAYER-GA', 'CLICK PAUSE', location.href);
                myStreamingTag.stop();
            },
            onComplete: function(){
                if(ga) ga('send', 'event', 'JWPLAYER-GA', 'COMPLETE', location.href);
                myStreamingTag.stop();
            }
        }
    });
