body:
a {

	text-decoration: none;
}.vorteile-list {
    margin: 1rem auto; 
    width: 90%;
}.vorteile {
	background-image: url("/assets/themes/etd18/images/HakenBlau.png");
	background-position: left center;
	background-repeat: no-repeat;
    background-size: 20px;
	display: block;
	padding-left: 36px;
    margin-bottom: 0.75rem;
    min-height: 2rem;
	font-size: 12px;
	line-height: 1.25;
}.vorteile > span {
	font-weight: 700;
	display: block;
    font-size: 16px;;
}.angebot h2 {
	color: #FF8800;
	text-align: center;
}.abo button {
	background-color: #FF8800;
    padding: 8px;
    font-size: 24px;
    margin-top: 10px;
    border-radius: 7px;
    color: #FFF;
}.etd-premium-word {
    font-family: 'Pinyon Script';
    font-weight: 700;
    font-size: 150%;
}a {
	color: #005081;
}.lp_paid-content .lp_full-content {
    opacity: 0.3;
}
html body .lp_purchase-overlay {
    font-family: 'Open Sans' !important;
}@media only screen and (min-width: 35.5rem) {
    .vorteile-list {
        width: 67%;
    }
}
